mapper.select:
SELECT * FROM (SELECT rownum AS rn, a.* FROM (
SELECT BOARD_NO , TITLE , CONTENT , WRITER , WRITE_DATE , CLICK_CNT , IMAGE FROM board b
ORDER BY title
) a where rownum <=#{svo.end} b where rn >= #{svo.start}

mapper.insert:

 			select board_seq.nextval from dual

 		insert into board
 					(BOARD_NO
					, TITLE
					, CONTENT
					, WRITER
					, WRITE_DATE
					, CLICK_CNT
					, IMAGE)
		values (
				#{boardNo}
				, #{title}
				, #{content}
				, #{writer}
				, #{writerDate}
		)
